def fetch_photo_content(
    photo_resource_identifier,
    maximum_image_width_pixels=None,
    maximum_image_height_pixels=None,
    prevent_redirection_and_get_json=False
):
    """
    Retrieves photo media by resource name.

    Args:
        photo_resource_identifier (str): The resource name of a photo, formatted as
            "places/{place_id}/photos/{photo_reference}/media". (Required)
        maximum_image_width_pixels (Optional[int]): The maximum desired photo width (range 1–4800).
        maximum_image_height_pixels (Optional[int]): The maximum desired photo height (range 1–4800).
        prevent_redirection_and_get_json (bool): If True, skips HTTP redirects and returns JSON data. Defaults to False.

    Returns:
        List[Dict[str, str]]: A list of photo media objects, each containing:
            - photoUri (str): The URL to the photo media.
            - name (str): The full resource name of the photo.

    Raises:
        ValueError: If the resource photo_resource_identifier does not match the expected format.
        ValueError: If neither maximum_image_width_pixels nor maximum_image_height_pixels is specified.
    """
    from google_maps.Places.Photos import getMedia
    return getMedia(
        name=photo_resource_identifier,
        maxWidthPx=maximum_image_width_pixels,
        maxHeightPx=maximum_image_height_pixels,
        skipHttpRedirect=prevent_redirection_and_get_json
    )
